def isSubtree(self, root: Optional[TreeNode], subRoot: Optional[TreeNode]) -> bool:
        # Note:
        # BFS order: level by level
        # 1. For each node:
        #    - If value matches subRoot root, run isSameTree
        # 3. Uses queue to iterate nodes iteratively

        def isSameTree(s, t):

            # Base case: leaf -> match
            if not s and not t:
                return True

            # no longer match: propagate error upwards
            if not s or not t:
                return False  
            if s.val != t.val:
                return False

            # validate subtrees
            return isSameTree(s.left, t.left) and isSameTree(s.right, t.right)

        # Empty check:
        # empty subRoot is always a subtree
        if not subRoot:
            return True

        # Empty check:
        # empty root cannot have subtrees
        if not root:
            return False

        queue = deque([root])
        while queue:
            node = queue.popleft()
            if node.val == subRoot.val and isSameTree(node, subRoot):
                return True
            if node.left:
                queue.append(node.left)
            if node.right:
                queue.append(node.right)

        return False

